import React, { Component } from 'react';
import { Butt } from '../components/Butt';
import { FaQuestionCircle, FaListAlt, FaPoll} from 'react-icons/fa'
import uuid from 'uuid';
import AnsI from '../components/AnsI';

export default class Mult extends React.Component {



  onClose = (e) => {
  	this.props.onClose && this.props.onClose(e);
  }


  render() {

  	const { ansI, handleChange, handleSubmit, ansIs, cleaRans, handleDelete, handleEdit} = this.props;


  	if (!this.props.show) {
  		return null;
  	}
	return( 
		<div className='moda-back'>
			<div className='moda-fron'>
				{this.props.children}


					<form onSubmit={(e) =>{handleSubmit(e)}}>

						<div className="crea-titl">
		  					<h4>multiple choice</h4>		   
	        						<div />
	    				</div>
						<div className="input-group mb-3">
								<div className="input-group-append">
									<div className="input-group-text bg-primary text-warning bg-light">
										<FaQuestionCircle/>
									</div>
								</div>
								<input type="text"
									className="form-control"
									placeholder="Type the Question"							
								/>
						</div>	


						<div className="input-group">
							<div className="input-group-append">
								<div className="input-group-text bg-primary bg-light text-secondary">
									<FaListAlt/>
								</div>
							</div>
							<input type="text"
								className="form-control text-capitalize"
								placeholder="Type the Answers"
								value={ansI}
								onChange={handleChange}
							/>
						</div>

						<Butt className="btn-upper mt-3 mb-3" type="submit"> 						
							Add Answers 
						</Butt>

						<Butt onClick={(e) => { this.onClose(e)} }>
							Close
						</Butt>							
					</form>	



				<ul className="list-group">	    	
				  	{ansIs.map(ansI => {
				  		return (
					  		<AnsI 
					  		key={ansI.id}
					  		ansW={ansI.ansW} 
					  		handleDelete={()=> handleDelete(ansI.id)}
					  		handleEdit={()=> handleEdit(ansI.id)}

				  			/>
				  		);
				  	})}
					<Butt
			          type="button"
			          className="btn-upper mt-3 mb-3 text-capitalize mt-5"
			          onClick={cleaRans} type="submit"
			        >
			          clear list
			        </Butt>

				</ul>				

			</div>
		</div>
	)
   }
}
