import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import { Butt } from '../components/Butt';
import { Head3 } from '../styles/Head3';
import { Head4 } from '../styles/Head4';
import { FaThinkPeaks, FaThList, FaStackExchange, FaPoll} from 'react-icons/fa';
import Titl from '../components/Titl';
import AnsI from '../components/AnsI';
import Mult from '../modal/Mult';
import uuid from 'uuid';

export default class Crea extends Component {
	state={
			crea: [
		{
			icon: <FaPoll/>,
			titl: 'multiple choice',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		},
		{
			icon: <FaStackExchange/>,
			titl: 'Q & A',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		},
		{
			icon: <FaThinkPeaks/>,
			titl: 'Survey',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		  	}
		]			
	};

	render() {
		return (
		<section>
		 <div className="how-proc">
		  	<Titl titl="create poll" className=".sect-titl-mode"/>
		  	  <div className="crea-center">
		  	    {this.state.crea.map((item, index) => {
		  	    	return (
		  	    	<article key={index} className="crea">		  	    		

                  		<span >
                  			{item.icon}
                  		</span>



                  		<Butt className="btn-upper" type="button"> 
                    		{item.titl}
                  		</Butt>	

		  	    	</article>

		  	    	);
		  	    })}
		 	  </div>


		  </div>
	    </section>
		);
	}


}
