import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import { Butt } from '../components/Butt';
import { Head3 } from '../styles/Head3';
import { Head4 } from '../styles/Head4';
import { FaThinkPeaks, FaThList, FaStackExchange, FaPoll} from 'react-icons/fa';
import Titl from '../components/Titl';
import AnsL from '../components/AnsL';
import Mult from '../modal/Mult';
import uuid from 'uuid';

export default class Crea extends Component {

	state={
		crea: [
		{
			icon: <FaPoll/>,
			titl: 'multiple choice',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		},
		{
			icon: <FaStackExchange/>,
			titl: 'Q & A',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		},
		{
			icon: <FaThinkPeaks/>,
			titl: 'Survey',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		  	}
		],

		ansIs:[],
			id:uuid(),
			ansI:"",
			editansI:false
		};

		handleChange = (e) => {
			this.setState({
				ansI: e.target.value
			});
		};

		handleSubmit = (e) => {
			e.preventDefault();


		const newansI = {
			id: this.state.id,
			ansW: this.state.ansI
		};

		console.log(newansI);

		const updatedansIs = [...this.state.ansIs,newansI];

		this.setState({
			ansIs:updatedansIs,
			ansI:'',
			id:uuid(),
			editansI:false
		});	
	};

	cleaRans = () => {
		this.setState({
			ansIs: []
		});

	};


	handleDelete = (id) =>{
		const filTansIs = this.state.ansIs.filter(ansI => 
			ansI.id !== id);
			this.setState({
			ansIs: filTansIs				
		});
	};

	Edit = (id) =>{ 
		const filTansIs = this.state.ansIs.filter(ansI => 
			ansI.id !== id);

		const seleansI = this.state.ansIs.find(ansI => 
			ansI.id === id);

			this.setState({
			ansIs: filTansIs,
			ansI: seleansI.ansW,
			editansI: true,
      		id: id
		});
console.log(seleansI.ansW);
	};



	showModal = () => {
		this.setState({
		...this.state,
		 show: !this.state.show
		});
	}


	render() {
		return (
		<section>
		 <div className="how-proc">
		  	<Titl titl="create poll" className=".sect-titl-mode"/>
		  	  <div className="crea-center">
		  	    {this.state.crea.map((item, index) => {
		  	    	return (
		  	    	<article key={index} className="crea">		  	    		

                  		<span onClick={this.showModal}>
                  			{item.icon}
                  		</span>

                  		<h6	onClick={this.showModal}>

                  		</h6> 

                  		<Butt className="btn-upper" type="button" 
                  			onClick={this.showModal}> 
                    		{item.titl}
                  		</Butt>	

		  	    	</article>

		  	    	);
		  	    })}
		 	  </div>
	 	  	<Mult 
	 	  		ansI={this.setState.ansI}
	 	  		handleChange={this.handleChange}
	 	  		handleSubmit={this.handleSubmit}
	 	  		ansIs={this.state.ansIs}
				onClose={this.showModal}
	 	  		show={this.state.show}
    			ansIs={this.state.ansIs}
    			cleaRans={this.cleaRans}
    			handleDelete={this.handleDelete}
    			Edit={this.Edit} />	  	    	

		  </div>
	    </section>
		);
	}
}
