import React, { Component } from 'react';
import { NavLink } 			from 'react-router-dom';
import { Head5 } 			from '../styles/Head5'
import Title				from '../components/Title';
import { FaLaptopCode, 
		 FaPaintBrush, 
		 FaDatabase} 		from 'react-icons/fa'

import Front_End 			from '../components/Front_End';
import Graphic_Design 		from '../components/Graphic_Design';
import Programming 			from '../components/Programming';

import { followData } 		from "../data/followData";
import FollowIcon           from '../components/Follow_Icon';

export default class Follow extends Component {

	state = {
		follows: followData
	};

	render() {
	    return (

	      <section> 	        
	        <div className="how-portfolio">
	          <Title title="Follow Me" />

	          	<Head5>   Want to chat about interactive web development?
	          		Iâ€™d love to hear from you. Drop me a line via email or your preferred social media platform.
	          	</Head5> 	

                <ul>
            	  {this.state.follows.map((item, index) => {
              		return (
              		  <li key={index}>
						<a className="nav-link p-project" href={item.source_url} 
		    			   target="_blank" rel={item.rel}> {item.name}                          
                        </a>     		  	
              			<h6 className="nav-link">{item.title}</h6>
                  		<Head5> {item.detail}</Head5>
              		  </li>
              		);
            	  })}
                </ul>       
	        </div>
	        <div className="icon">
          		<FollowIcon />
        	</div>
	      </section>


		);
  	}
}
