import React, { Component } from 'react';
import { MdTabletMac, MdGridOn } from "react-icons/md";
import { GiConsoleController, GiJigsawPiece, GiOBrick, GiPerspectiveDiceThree} from "react-icons/gi";

export default class Programming extends Component {
  state={
    proc: [
    {
      icon:   <GiConsoleController/>,
      title:  'Arcade Game', 
      info: "This popular game reinforces players to have a better eye-hand coordination. Languages: HTML, CSS and JavaScript.",
      github: "https://rrcanlas.github.io/ArcAde-gAme/",
      liveDemo: "https://github.com/rrcanlas/ArcAde-gAme",
      rel: "noopener noreferrer"
    },
    {
      icon: <MdGridOn/>,
      title: 'Memory Game',
      info: "A game to find matching pairs of cards until all pairs are found. The app has a timer and scoring functions. Languages: HTML, CSS, JavaScript",
      github: "https://rrcanlas.github.io/MeMory-gaMe/",
      liveDemo: "https://github.com/rrcanlas/MeMory-gaMe",
      rel: "noopener noreferrer"
    },
    {
      icon: <MdTabletMac/>,
      title: 'Mock-Up',
      github: "https://rrcanlas.github.io/moCkUp-tO-artiCle/",
      liveDemo: "https://rrcanlas.github.io/moCkUp-tO-artiCle/",
      info: "A recent mockup of a website (and a copy of the text) which was translated into an HTML.",
      rel: "noopener noreferrer"
    },
    {
      icon: <GiOBrick/>,
      title: 'Pixel Art MAker',
      info: "Part of Grow-With-Google Udacity Front-End NanoDegree. Can be viewed on Desktop. Languages: HTML, CSS, JavaScript",
      source_url: "https://rrcanlas.github.io/piXel-aRt-maKeR/",
      code: "https://github.com/rrcanlas/piXel-aRt-maKeR",
      github: "hhttps://rrcanlas.github.io/piXel-aRt-maKeR/",
      liveDemo: "https://github.com/rrcanlas/ArcAde-gAme",
      rel: "noopener noreferrer"
    }
    ]
  }



  render() {
    return (

      <section className="about"> 

        <div className="how-portfolio">

              <div className="portfolio-center-nav">
                {this.state.proc.map((item, index) => {
                  return (
                  <article key={index} className="portfolio-nav">
                    <span className="nav-link">{item.icon}</span>
                    <h6 className="nav-link">{item.title}</h6>

                  </article>
                  );
                })}
              </div>
        </div>
    </section>

    );
  }

}
