import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import { Butt } from '../components/Butt';
import { Head3 } from '../styles/Head3';
import { Head4 } from '../styles/Head4';
import { FaThinkPeaks, FaThList, FaStackExchange, FaPoll} from 'react-icons/fa';
import Titl from '../components/Titl';
import AnsI from '../components/AnsI';
import Mult from '../components/Mult';
import "bootstrap/dist/css/bootstrap.min.css";
import uuid from "uuid";

export default class Crea extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [],
      nextId: 3,


			crea: [
		{
			icon: <FaPoll/>,
			titl: 'multiple choice',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		},
		{
			icon: <FaStackExchange/>,
			titl: 'Q & A',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		},
		{
			icon: <FaThinkPeaks/>,
			titl: 'Survey',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		  	}
		],

    };

    this.addTodo = this.addTodo.bind(this);
    this.removeTodo = this.removeTodo.bind(this);
  }

  addTodo(todoText) {
    let todos = this.state.todos.slice();
    todos.push({id: this.state.nextId, text: todoText});
    this.setState({
      todos: todos,
      nextId: ++this.state.nextId
    });
  }

  removeTodo(id) {
    this.setState({
        todos: this.state.todos.filter((todo, index) => todo.id !== id)
      });
  }


  showModal = () => {
	this.setState({
	...this.state,
	 show: !this.state.show
	});
  }

  render() {
    return (
		<div className="how-proc">
		  	<Titl titl="create poll" className=".sect-titl-mode"/>
		  	  <div className="crea-center">
		  	    {this.state.crea.map((item, index) => {
		  	    	return (
		  	    	<article key={index} className="crea">		  	    		

                  		<span onClick={this.showModal}>
                  			{item.icon}
                  		</span>

                  		<h6	onClick={this.showModal}>

                  		</h6> 

                  		<Butt className="btn-upper" type="button" 
                  			onClick={this.showModal}> 
                    		{item.titl}
                  		</Butt>	

		  	    	</article>

		  	    	);
		  	    })}
		 	  </div>        
          <Mult todoText="" addTodo={this.addTodo} />
          <ul>
            {
              this.state.todos.map((todo) => {
                return <AnsI todo={todo} key={todo.id} id={todo.id} removeTodo={this.removeTodo}/>
              })
            }
          </ul>
        </div>

    );
  }
}
