import React, { Component }      from 'react';
import { MdTabletMac, MdGridOn } from "react-icons/md";
import { GiConsoleController, 
         GiJigsawPiece, 
         GiOBrick, 
         GiPerspectiveDiceThree} from "react-icons/gi";

import { Butt  }                 from '../styles/Butt';

export default class Front_End extends Component {
	state={
    proc: [
    {
      id: 1,
      icon:   <GiConsoleController/>,
      title:  'Arcade Game', 
      info: "This popular game reinforces players to have a better eye-hand coordination. Languages: HTML, CSS and JavaScript.",
      github: "https://github.com/rrcanlas/ArcAde-gAme",
      liveDemo: "https://rrcanlas.github.io/ArcAde-gAme/",
      rel: "noopener noreferrer"
    },
    {
      id: 2,
      icon: <MdGridOn/>,
      title: 'Memory Game',
      info: "A game to find matching pairs of cards until all pairs are found. The app has a timer and scoring functions. Languages: HTML, CSS, JavaScript",
      github: "https://github.com/rrcanlas/MeMory-gaMe",
      liveDemo: "https://rrcanlas.github.io/MeMory-gaMe/",
      rel: "noopener noreferrer"
    },
    {
      id: 3,
      icon: <MdTabletMac/>,
      title: 'Mock-Up',
      github: "https://github.com/rrcanlas/moCkUp-tO-artiCle",
      liveDemo: "https://rrcanlas.github.io/moCkUp-tO-artiCle/",
      info: "A recent mockup of a website (and a copy of the text) which was translated into an HTML.",
      rel: "noopener noreferrer"
    },
    {
      id: 4,
      icon: <GiOBrick/>,
      title: 'Pixel Art MAker',
      info: "Part of Grow-With-Google Udacity Front-End NanoDegree. Can be viewed on Desktop. Languages: HTML, CSS, JavaScript",      
      github: "https://github.com/rrcanlas/piXel-aRt-maKeR",
      liveDemo: "https://rrcanlas.github.io/piXel-aRt-maKeR/",
      rel: "noopener noreferrer"
    }
    ]
  }


  render() {
    return (

      <section className="about"> 

        <div className="how-portfolio">

              <div className="portfolio-center">
                {this.state.proc.map((item, index) => {
                  return (
                  <article key={index} className="portfolio">
                    <span className="nav-link">{item.icon}</span>
                    <h6 className="nav-link">{item.title}</h6>
                      <form className="nav-link">
                        <Butt className="btn-upper" type="submit">
                        <a className="nav-link" href={item.github} 
                        target="_blank" rel={item.rel}>  GITHUB
                         </a>
                        </Butt>

                        <Butt className="btn-upper" type="submit">
                        <a href={item.liveDemo} 
                        target="_blank" rel={item.rel}> LIVE DEMO
                         </a>
                         </Butt>
                      </form>
                    <p>{item.info}</p>

                  </article>
                  );
                })}
              </div>
        </div>
    </section>

    );
  }

}
