import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import { Butt } from '../components/Butt';
import { Head3 } from '../styles/Head3';
import { Head4 } from '../styles/Head4';
import { FaThinkPeaks, FaThList, FaStackExchange, FaPoll} from 'react-icons/fa';
import Titl from '../components/Titl';
import { Modal } from 'react-bootstrap';
import { useState } from 'react';

export default class Crea extends Component {
	state={
		crea: [
		{
			icon: <FaPoll/>,
			titl: 'multiple choice',
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		},
		{
			icon: <FaStackExchange/>,
			titl: 'Q & A',
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		},
		{
			icon: <FaThinkPeaks/>,
			titl: 'Survey',
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		  	}
		]
	}
clickMe(){
	const [show, setShow] = useState(false);

	<Modal
        show={show}
        onHide={() => setShow(false)}
        dialogClassName="modal-90w"
        aria-labelledby="example-custom-modal-styling-title"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-custom-modal-styling-title">
            Custom Modal Styling
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>
            Ipsum molestiae natus adipisci modi eligendi? Debitis amet quae unde
            commodi aspernatur enim, consectetur. Cumque deleniti temporibus
            ipsam atque a dolores quisquam quisquam adipisci possimus
            laboriosam. Quibusdam facilis doloribus debitis! Sit quasi quod
            accusamus eos quod. Ab quos consequuntur eaque quo rem! Mollitia
            reiciendis porro quo magni incidunt dolore amet atque facilis ipsum
            deleniti rem!
          </p>
        </Modal.Body>
      </Modal>

}

	render() {
		return (
		<section>
		 <div className="how-proc">
		  	<Titl titl="create poll" />
		  	  <div className="crea-center">
		  	    {this.state.crea.map((item, index) => {
		  	    	return (
		  	    	<article key={index} className="crea">
		  	    		<a onClick={this.clickMe.bind(this)}>
		  	    		<span>{item.icon}</span>
		  	    		<h6>{item.titl}</h6>
		  	    		</a>
		  	    	</article>
		  	    	);
		  	    })}
		  	  </div>
				</div>
	    </section>
		);
	}
}
