import React, { Component } from 'react';
import { Butt } from '../components/Butt';
import Titl_Crea from '../components/Titl_Crea';
export default class Mult extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      quesTion:"",
      newItem: "",
      list: []
    };
  }

  //incorporating local storage 
  componentDidMount() {
    this.hydrateStateWithLocalStorage();

    // add event listener to save state to localStorage
    // when user leaves/refreshes the page
    window.addEventListener(
      "beforeunload",
      this.saveStateToLocalStorage.bind(this)
    );
  }

  componentWillUnmount() {
    window.removeEventListener(
      "beforeunload",
      this.saveStateToLocalStorage.bind(this)
    );

    // saves if component has a chance to unmount
    this.saveStateToLocalStorage();
  }

  hydrateStateWithLocalStorage() {
    // for all items in state
    for (let key in this.state) {
      // if the key exists in localStorage
      if (localStorage.hasOwnProperty(key)) {
        // get the key's value from localStorage
        let value = localStorage.getItem(key);

        // parse the localStorage string and setState
        try {
          value = JSON.parse(value);
          this.setState({ [key]: value });
        } catch (e) {
          // handle empty string
          this.setState({ [key]: value });
        }
      }
    }
  }

  saveStateToLocalStorage() {
    // for every item in React state
    for (let key in this.state) {
      // save to localStorage
      localStorage.setItem(key, JSON.stringify(this.state[key]));
    }
  }

  updateInput(key, value) {
    // update react state
    this.setState({ [key]: value });
  }

  addItem() {
    // create a new item with unique id
    const newItem = {
      id: 1 + Math.random(),
      value: this.state.newItem.slice()

    };

    // copy current list of items
    const list = [...this.state.list];

    // add the new item to the list
    list.push(newItem);

    // update state with new list, reset the new item input
    this.setState({
      list,
      newItem: ""
    });
  }

  deleteItem(id) {
    // copy current list of items
    const list = [...this.state.list];
    // filter out the item being deleted
    const updatedList = list.filter(item => item.id !== id);

    this.setState({ list: updatedList });
  }

  onClose(e) {
    this.props.onClose && this.props.onClose(e);
  }


//render starts here


  render() {
    if (!this.props.show) {
      return null;
    }
    return (
      <div className='moda-back'>
        <div className='moda-fron'>
          {this.props.children}




          <Titl_Crea titl="multiple choice" className="app-title"></Titl_Crea>

            <div className="middle">
              <Butt onClick={(e) => { this.onClose(e)}}>
              Go Back
            </Butt> 

              <div className="instruction"> Ask a question and let participants 
                choose from a list of answers.
              </div>

              <div class="input-group mb-1" >
                <input
                  type="text"
                  class="form-control"
                  placeholder="Type the Question"
                  className="txt-ques"
                  readonly="readonly"
                />
              </div>

              <span class="input-group mb-1">
                <input
                  type="text"
                  class="form-control"
                  placeholder="Type List of Answers"
                  className="txt-answ"
                  value={this.state.newItem}
                  onChange={e => this.updateInput("newItem", e.target.value)}
                />
                <div class="input-group-append">
                  <span class="input-group-text" type="button"
                    onClick={() => this.addItem()}
                    disabled={!this.state.newItem.length}
                  >
                  <i class="material-icons"> + </i>
                  </span>
                </div>
              </span>





              <ul>
                {this.state.list.map(item => {
                  return (
                    <li key={item.id}>
                      <input className="list" 
                      type="text" 
                      value={item.value} />

                      <button className="btn btn-floating" onClick={() => this.deleteItem(item.id)}>
                        <i class="material-icons">x</i>
                      </button>
                    </li>
                  );
                })}
              </ul>
            </div>

        </div>
      </div>
    );
  }
}
