import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import { Butt  } from '../components/Butt'
import { Head3 } from '../styles/Head3'
import { Head4 } from '../styles/Head4'
import { Head5 } from '../styles/Head5'
import { FaLaptopCode, FaPaintBrush, FaDatabase} from 'react-icons/fa'
import Titl from './Titl';
import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardText, CardActions, Button, CardMenu, IconButton } from 'react-mdl';



export default class Info extends Component {
	state={
		proc: [
		{
			icon: <FaLaptopCode/>,
			titl: 'Front-end',
			activeTab: 0,
		},
		{
			icon: <FaPaintBrush/>,
			titl: 'Graphic Design',
			activeTab: 1,
		},
		{
			icon: <FaDatabase/>,
			titl: 'Programming',
			activeTab: 2,
		  	}
		]
	}






	render() {
		return (

		  <section className="about"> 

				<div className="how-portfolio">

				  	  <div className="portfolio-center-nav">
				  	    {this.state.proc.map((item, index) => {
				  	    	return (
				  	    	<article key={index} className="portfolio-nav">
				  	    		<span className="nav-link">{item.icon}</span>
				  	    		<h6 className="nav-link">{item.titl}</h6>

				  	    	</article>
				  	    	);
				  	    })}
				  	  </div>
				</div>
		</section>

		);
	}

	toggleCategories() {

    if(this.state.activeTab === 0){
      return(
      	<div><h1>This is React</h1></div>
      	)
    } else if(this.state.activeTab === 1) {
      return (
        <div><h1>This is Angular</h1></div>
      )
    } else if(this.state.activeTab === 2) {
      return (
        <div><h1>This is VueJS</h1></div>
      )    
    }

  	}

	render() {
    return(
      <div>



          <Grid>
            <Cell col={12}>
              <div className="content">{this.toggleCategories()}</div>
            </Cell>
          </Grid>


      </div>
    )
  }



}
