import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import { Butt } from '../components/Butt';
import { Head3 } from '../styles/Head3';
import { Head4 } from '../styles/Head4';
import { FaThinkPeaks, FaThList, FaStackExchange, FaPoll} from 'react-icons/fa';
import Titl from '../components/Titl';
import AnsI from '../components/AnsI';
import Mult from '../modal/Mult';
import uuid from 'uuid';

export default class Crea extends Component {
	state={
			crea: [
		{
			icon: <FaPoll/>,
			titl: 'multiple choice',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		},
		{
			icon: <FaStackExchange/>,
			titl: 'Q & A',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		},
		{
			icon: <FaThinkPeaks/>,
			titl: 'Survey',
			show: false,
			deta: 'Lorem ipsum dolor sit amet consectetuer adipiscing elit.Praesent vestibulum molestie lacus. Aenean nonummy hendrerit mauris. Phasellus porta.'
		  	}
		]			
	};

	render() {
		return (
		<section>
		 <div className="how-proc">
		  	<Titl titl="create poll" className=".sect-titl-mode"/>
		  	  <div className="crea-center">
		  	    {this.state.crea.map((item, index) => {
		  	    	return (
		  	    	<article key={index} className="crea">		  	    		

                  		<span >
                  			{item.icon}
                  		</span>



                  		<Butt className="btn-upper" type="button"> 
                    		{item.titl}
                  		</Butt>	

		  	    	</article>

		  	    	);
		  	    })}
		 	  </div>


		  </div>
	    </section>
		);
	}

	constructor(props) {
    super(props);
	    this.state = {
	      newItem: "",
	      list: []
	    };
  	}




	 //incorporating local storage 
  componentDidMount() {
    this.hydrateStateWithLocalStorage();

    // add event listener to save state to localStorage
    // when user leaves/refreshes the page
    window.addEventListener(
      "beforeunload",
      this.saveStateToLocalStorage.bind(this)
    );
  }

  componentWillUnmount() {
    window.removeEventListener(
      "beforeunload",
      this.saveStateToLocalStorage.bind(this)
    );

    // saves if component has a chance to unmount
    this.saveStateToLocalStorage();
  }

  hydrateStateWithLocalStorage() {
    // for all items in state
    for (let key in this.state) {
      // if the key exists in localStorage
      if (localStorage.hasOwnProperty(key)) {
        // get the key's value from localStorage
        let value = localStorage.getItem(key);

        // parse the localStorage string and setState
        try {
          value = JSON.parse(value);
          this.setState({ [key]: value });
        } catch (e) {
          // handle empty string
          this.setState({ [key]: value });
        }
      }
    }
  }

  saveStateToLocalStorage() {
    // for every item in React state
    for (let key in this.state) {
      // save to localStorage
      localStorage.setItem(key, JSON.stringify(this.state[key]));
    }
  }

  updateInput(key, value) {
    // update react state
    this.setState({ [key]: value });
  }

  addItem() {
    // create a new item with unique id
    const newItem = {
      id: 1 + Math.random(),
      value: this.state.newItem.slice()

    };

    // copy current list of items
    const list = [...this.state.list];

    // add the new item to the list
    list.push(newItem);

    // update state with new list, reset the new item input
    this.setState({
      list,
      newItem: ""
    });
  }

  deleteItem(id) {
    // copy current list of items
    const list = [...this.state.list];
    // filter out the item being deleted
    const updatedList = list.filter(item => item.id !== id);

    this.setState({ list: updatedList });
  }

  render() {
    return (
      <div>

      <h1 className="app-title">MY LIST</h1>

        <div className="container">
        <div
          style={{
            padding: 30,
            textAlign: "left",
            maxWidth: 500,
            margin: "auto"
          }}
        >
          Add an Item...
          <br />
          <input
            type="text"
            placeholder="Type item here"
            value={this.state.newItem}
            onChange={e => this.updateInput("newItem", e.target.value)}
          />
          <button
            className="add-btn btn-floating"
            onClick={() => this.addItem()}
            disabled={!this.state.newItem.length}
          >
            <i class="material-icons"> + </i>
          </button>
          <br /> <br />
          <ul>
            {this.state.list.map(item => {
              return (
                <li key={item.id}>
                  {item.value}
                  <button className="btn btn-floating" onClick={() => this.deleteItem(item.id)}>
                    <i class="material-icons">x</i>
                  </button>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
      </div>
    );
  }
}
