import React, { Component } from 'react';
import { NavLink } 			from 'react-router-dom';
import { Head5 } 			from '../styles/Head5'
import Title				from '../components/Title';
import { FaLaptopCode, FaPaintBrush, FaDatabase} from 'react-icons/fa'

export default class UX_Design extends Component {

	state={
		ux: [
		{
			id: 1,
			title: 'React',
			link:  'Front-End',
			detail: 'The goal of the Front End Web Developer Nanodegree program is to equip learners with the unique skills they need to build and develop a variety of websites and applications. Graduates of this Nanodegree program will be able to construct responsive websites using CSS, Flexbox and CSS Grid, develop interactive websites and UI (User Interface) applications using JavaScript and HTML, and connect a web application.'
		},
		{
			id: 2,
			title: 'HTML',
			link:  'Front-End',
			detail: 'Capture attention with beautiful, high-impact visuals. Adjust clarity, color and tone and create image manipulation effects or perfect your graphic design work. Take your pictures to the next level with the most powerful image editing software for web, desktop, and mobile. Whether you are a novice or pro photographer, designer or artist, you will have the tools to take all your projects to the next level.'
		},
		{
			id: 3,
			title: 'WordPress',
			link:  'Front-End',
			detail: 'Visual Basic (or VB) is a programming language developed by Microsoft that runs on the .Net Framework. With Visual Basic you can build Windows applications, web applications and Windows phone applications. Programs developed in Visual Basic will only run on a Windows Operating System. Visual Basic is easy to learn, fun and powerful!'
		  	}
		]
	}


	render() {
	    return (	      
	      <section> 	        
	        <div className="how-portfolio">
	          <Title title="Front-end" />	              
                <ul>
            	  {this.state.ux.map((item, index) => {
              		return (
              		  <li key={index}>
              		  	<NavLink to="/{item.link}" className="nav-link title-project" >
									{item.title}
						</NavLink>
              			<h6 className="nav-link title-project">{item.title}</h6>
                  		<p className="p-project">{item.detail}</p>
              		  </li>
              		);
            	  })}
                </ul>       
	        </div>
	      </section>	      
		);
  	}
}
