import React, { Component } from 'react';
import { Butt } from '../components/Butt';
import { FaQuestionCircle, FaListAlt, FaPoll} from 'react-icons/fa'
import AnsL from '../components/AnsL';
import uuid from 'uuid';

export default class Mult extends React.Component {



  onClose = (e) => {
  	this.props.onClose && this.props.onClose(e);
  }

  render() {

  	const { ansI, handleChange, handleSubmit, ansIs } = this.props;

  	const newansI = {
			id: this.state.id,
			ansW: this.state.ansI
		};

		console.log(newansI);

		const updatedansIs = [...this.state.ansIs,newansI];

		this.setState({
			ansIs:updatedansIs,
			ansI:'',
			id:uuid(),
			editansI:false
		});

  	if (!this.props.show) {
  		return null;
  	}
	return( 
		<div className='moda-back'>
			<div className='moda-fron'>
				{this.props.children}

			<div className="card card-body my-3">
				<form onSubmit={handleSubmit}>
				<div className="input-group mb-3">
						<div className="input-group-append">
							<div className="input-group-text bg-primary text-warning bg-light">
								<FaQuestionCircle/>
							</div>
						</div>
						<input type="text"
							className="form-control"
							placeholder="Type the Question"							
						/>
					</div>	


					<div className="input-group">
						<div className="input-group-append">
							<div className="input-group-text bg-primary bg-light text-secondary">
								<FaListAlt/>
							</div>
						</div>
						<input type="text"
							className="form-control"
							placeholder="Type the Answers"
							value={ansI}
							onChange={handleChange}
						/>
					</div>	
					<Butt className="btn-upper mt-3 mb-3" type="submit"> 

						Add Answers 
					</Butt>

					<Butt onClick={(e) => { this.onClose(e)}}>
						Close
					</Butt>

					<AnsL ansIs={this.state.ansIs}/>

				</form>				
			</div>




			</div>
		</div>
	)
   }
}
