import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import { Butt } from '../components/Butt';
import { Head3 } from '../styles/Head3';
import { Head4 } from '../styles/Head4';
import { FaThinkPeaks, FaThList, FaStackExchange, FaPoll} from 'react-icons/fa';
import Titl from '../components/Titl';
import AnsI from '../components/AnsI';
import Mult from '../modal/Mult';
import uuid from 'uuid';
import {Modal, Text, TouchableHighlight, View, Alert} from 'react-native';

export default class Crea extends Component {

  state = {
    modalVisible: false,
  };

  setModalVisible(visible) {
    this.setState({modalVisible: visible});
  }

  render() {
    return (
      <View style={{marginTop: 22}}>
        <Modal
          animationType="slide"
          transparent={false}
          visible={this.state.modalVisible}
          onRequestClose={() => {
            Alert.alert('Modal has been closed.');
          }}>
          <View style={{marginTop: 22}}>
            <View>
              <Text>Hello World!</Text>

              <TouchableHighlight
                onPress={() => {
                  this.setModalVisible(!this.state.modalVisible);
                }}>
                <Text>Hide Modal</Text>
              </TouchableHighlight>
            </View>
          </View>
        </Modal>

        <TouchableHighlight
          onPress={() => {
            this.setModalVisible(true);
          }}>
          <Text>Show Modal</Text>
        </TouchableHighlight>
      </View>
    );
  }
}
