import React, { Component } from 'react';
import { MdRemove, MdPlaylistAdd } from 'react-icons/md';
import { Butt_Add } from '../components/Butt_Add';
import { IoMdTrash,IoMdAddCircleOutline } from "react-icons/io";

export default class Poll_Choi extends Component {
render() {
	const { newItem } = this.props;
	return (
		<div>
		<input
          type="text"
          className="form-control text-capitalize"
          placeholder="Type List of Answers"
          className="txt-answ"
          value={this.state.newItem}
          onChange={e => this.updateInput("newItem", e.target.value)}
        />

        <div className="input-group-append">
          <Butt_Add
            onClick={() => this.addItem()}
            disabled={!this.state.newItem.length}
          >
          <IoMdAddCircleOutline/>
          </Butt_Add>
        </div>
        </div>




		)
	}
}
